    private void registerBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_registerBtnActionPerformed
        ValidateName nameValidate = new ValidateName();
        ValidateMobile mobileValidate = new ValidateMobile();
        ValidatePassword passwordValidate = new ValidatePassword();
        ValidateEmail emailValidate = new ValidateEmail();
        ValidateCountry countryValidation = new ValidateCountry();
        
        nameValidate.setNext(mobileValidate);
        mobileValidate.setNext(passwordValidate);
        passwordValidate.setNext(emailValidate);
        emailValidate.setNext(countryValidation);
        Details details = new Details(nametext, mobileTxt, passwordTxt, emailTxt, countryCombo, this);
        nameValidate.validate(details);
        if (details.isIsCompleteSuccessfuly()) {
            
            JOptionPane.showMessageDialog(this, "You Have Registered Successfuly\n"+nametext.getText().trim()+" Welcome to the system", "Complete", JOptionPane.ERROR_MESSAGE, userImage.getIcon());
            nametext.setText("Enter Your Name...");
            mobileTxt.setText("Enter Your Mobile...");
            passwordTxt.setText("Enter Your Password...");
            showHide.setText("ðŸ”’");
            passwordTxt.setEchoChar((char) 0);
            emailTxt.setText("Enter Your Email...");
            countryCombo.setSelectedIndex(0);
            
            nametext.setBorder(DEFAULT_BORDER);
            mobileTxt.setBorder(DEFAULT_BORDER);
            passwordTxt.setBorder(DEFAULT_BORDER);
            emailTxt.setBorder(DEFAULT_BORDER);
            countryCombo.setBorder(DEFAULT_BORDER);
            userImage.requestFocus();
            ImageIcon icon = new ImageIcon("src/images/user.png");
            userImage.setIcon(icon);
            
        }

    }//GEN-LAST:event_registerBtnActionPerformed

    private void showHideActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_showHideActionPerformed
        passwordTxt.requestFocus();
        if (showHide.getText().equals("ðŸ”’")) {
            showHide.setText("ðŸ”“");
            passwordTxt.setEchoChar((char) 0);
        } else {
            showHide.setText("ðŸ”’");
            passwordTxt.setEchoChar('â€¢');
        }
    }//GEN-LAST:event_showHideActionPerformed

    private void userImageMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_userImageMouseClicked
        JFileChooser fileChooser = new JFileChooser();
        fileChooser.setFileFilter(new FileNameExtensionFilter("Image files", "jpg", "png", "gif", "bmp"));
        fileChooser.setAcceptAllFileFilterUsed(false);
        
        int option = fileChooser.showOpenDialog(this);
        if (option == JFileChooser.APPROVE_OPTION) {
            File file = fileChooser.getSelectedFile();
            if (isImage(file)) {
                try {
                    BufferedImage img = ImageIO.read(file);
                    Image dimg = img.getScaledInstance(128, 128, Image.SCALE_SMOOTH);
                    ImageIcon imageIcon = new ImageIcon(dimg);
                    userImage.setIcon(imageIcon);
                } catch (IOException e) {
                    ImageIcon icon = new ImageIcon("src/images/picture.png");
                    JOptionPane.showMessageDialog(this, "Invalid Image", "Error", JOptionPane.ERROR_MESSAGE, icon);
                }
            } else {
                ImageIcon icon = new ImageIcon("src/images/picture.png");
                JOptionPane.showMessageDialog(this, "Invalid Image", "Error", JOptionPane.ERROR_MESSAGE, icon);
            }
        } else {
            
        }
    }//GEN-LAST:event_userImageMouseClicked
    private boolean isImage(File file) {
        boolean valid = true;
        try {
            Image image = ImageIO.read(file);
            if (image == null) {
                valid = false;
            }
        } catch (IOException ex) {
            valid = false;
        }
        return valid;
    }

